mapper.update:
UPDATE uaa_module
mod_name = #{mod_name}, mod_link = #{mod_link}, mod_level = #{mod_level}, father_mod = #{father_mod}, mod_type = #{mod_type}, mod_description = #{mod_description}, mod_order = #{mod_order}, mod_enable = #{mod_enable}
WHERE mod_id = #{mod_id}

mapper.select:
		SELECT * FROM uaa_module

			mod_level = #{mod_level}
			fater_mod = #{fater_mod}

 		ORDER BY mod_order ASC

		SELECT * FROM uaa_module module, uaa_roleModule roleMod

			roleMod.mod_id=module.mod_id
			AND module.mod_level=#{mod_level}

				AND	roleMod.role_id in

	       			 #{role}



 		ORDER BY module.mod_order ASC

		SELECT * FROM uaa_module module, uaa_roleModule roleMod

			roleMod.mod_id=module.mod_id
			AND module.father_mod=#{father_mod}

				AND	roleMod.role_id in

	       			 #{role}



 		ORDER BY module.mod_order ASC
