mapper.update:
UPDATE uaa_role
role_name = #{role_name}, role_level = #{role_level}, role_type = #{role_type}, role_description = #{role_description}, role_enable = #{role_enable}
WHERE role_id = #{role_id}

mapper.select:
SELECT * FROM uaa_role

	AND role_level = #{role_level}
	AND role_name LIKE '%${role_name}%'
	AND role_enable = #{role_enable}
	AND role_type = #{role_type}
